import React from "react";
import { useLocation, Link, Navigate } from "react-router-dom";


import logo from '../../images/logo-header.svg'
function Header({email, handleSignOut}) {
    const location = useLocation();
    const disableFooter = location.pathname === "/sign-up" || location.pathname === "/sign-in";
    function singOut () { 
        location.pathname === "/sign-up" ? <Navigate to='/sign-in' /> : location.pathname === "/sign-in" ? <Link to='/sign-up' /> : handleSignOut(); 
    }
    return (
        <header className="header">
            <img
                src={logo}
                alt="Логотип сайта Место"
                className="header__logo"
            />
            <div >
                <span className="header__email">{email}</span>
                <button className="header__button" onClick={singOut}>{location.pathname === "/sign-up" ? 'Войти' : location.pathname === "/sign-in" ? 'Регистрация' : 'Выйти'}</button> 
                {/* <Link className="header__button" to={location.pathname === "/sign-up" ? '/sign-in' : location.pathname === "/sign-in" ? '/sign-up' : () => handleSignOut()} >{location.pathname === "/sign-up" ? 'Войти' : location.pathname === "/sign-in" ? 'Регистрация' : 'Выйти'}</Link> */}
            </div>
        </header>
    )
}

export default Header;
