import useFormValidator from "../../utils/useFormValidator";
import PopupWithForm from "../PopupWithForms/PopupWithForm";

export default function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar}) {
    const {inputValues, inputErrors, isValid, isInputValid, handleChange, resetForm } = useFormValidator();

    function closePopup () {
        onClose();
        resetForm({nameInput: currentUser.name, jobInput: currentUser.about})
      }

    function handleSubmit(evt){
        evt.preventDefault();
        onUpdateUser({nameInput: inputValues.nameInput, jobInput: inputValues.jobInput});
      }


    return (
        <PopupWithForm name='update-avatar' title='Обновить аватар' textButton='Сохранить' isOpen={isOpen} onClose={closePopup} onSubmit={handleSubmit}>
          <input
            id="link-avatar"
            type="url"
            placeholder="Введите ссылку на аватар"
            className="popup__input popup__input_avatar_url"
            name="linkAvatar"
            required=""
          />
          <span
            id="link-avatar-error"
            className="popup__error popup__error_field_third"
          />
        </PopupWithForm>
    )
}
